INSERT INTO `mdiy_model` ( `id`,`model_json`,`model_table_name`,`model_name`,`model_type`,`model_field`,`model_custom_type`,`del`,`not_del` ) VALUES  ( 1563806494588611144,'{\"searchJson\":\"[\\n        // 启用站群\\n        {\'action\':\'and\', \'field\': \'SITE_ENABLE\', \'el\': \'eq\', \'model\': \'siteEnable\', \'name\': \'启用站群\', \'type\': \'switch\'},\\n        // 站群涉及表\\n        {\'action\':\'and\', \'field\': \'SITE_TABLES\', \'el\': \'eq\', \'model\': \'siteTables\', \'name\': \'站群涉及表\', \'type\': \'textarea\'},\\n]\\n\",\"isWebSubmit\":false,\"html\":\"\\n<template id=\\\"custom-model\\\">\\n    <el-form ref=\\\"form\\\" :model=\\\"form\\\" :rules=\\\"rules\\\" label-width=\\\"120px\\\" label-position=\\\"right\\\" size=\\\"small\\\">\\n\\n        <!--启用站群-->\\n    \\n        <el-form-item  label=\\\"启用站群\\\" prop=\\\"siteEnable\\\">\\n            <el-switch v-model=\\\"form.siteEnable\\\"\\n                       :disabled=\\\"false\\\">\\n            </el-switch>\\n                <div class=\\\"ms-form-tip\\\">\\n建议系统初始化时候决定是否启用<br/>\\n注:修改后需要重启系统生效                </div>\\n        </el-form-item>\\n   \\n        <!--站群涉及表-->\\t\\n\\t        <el-form-item  label=\\\"站群涉及表\\\" prop=\\\"siteTables\\\">\\n\\t            <el-input\\n                        type=\\\"textarea\\\" :rows=\\\"5\\\"\\n                        :disabled=\\\"false\\\"\\n                        :readonly=\\\"false\\\"\\n                        v-model=\\\"form.siteTables\\\"\\n                        :style=\\\"{width: \'100%\'}\\\"\\n                        placeholder=\\\"请输入站群涉及表\\\">\\n                </el-input>\\n                <div class=\\\"ms-form-tip\\\">\\n多个表逗号,分割，表中必须存在APP_ID字段<br/>\\n注:表有改动需要重新在站点管理初始化APPID                </div>\\n\\t        </el-form-item>\\n\\t   \\n    </el-form>\\n</template>\\n\",\"script\":\"\\n\\nvar custom_model = Vue.component(\\\"custom-model\\\",{\\nel: \'#custom-model\',\\ndata:function() {\\nreturn {\\n\\nmodelId:0,\\nmodelName: \\\"站群配置\\\",\\n//表单数据\\nform: {\\nlinkId:0,\\n                    // 启用站群\\n                    siteEnable:false,\\n                    // 站群涉及表\\n                    siteTables:\'cms_content,cms_category\',\\n},\\n\\nrules:{\\n},\\n}\\n},\\nwatch:{\\n            \\n            //启用站群       \\n            \\\"form.siteEnable\\\":function(nev,old){\\n               if(typeof(nev)==\'string\') {\\n                   this.form.siteEnable = (nev==\'true\');\\n               }                \\n            },\\n},\\ncomputed:{\\n},\\nmethods: {\\nvalidate:function(){\\nvar b = false\\nthis.$refs.form.validate(function(valid){\\nb = valid;\\n});\\nreturn b;\\n},\\nsave:function(callback) {\\nvar that = this;\\nvar url = this.formURL.save.url;\\nif (that.form.id > 0) {\\nurl = this.formURL.update.url;\\n}\\nthis.$refs.form.validate(function(valid) {\\nif (valid) {\\nvar form = JSON.parse(JSON.stringify(that.form));\\nform.modelId = that.modelId;\\nms.http.post(url, form).then(function (res) {\\nif(callback) {\\ncallback(res);\\n}\\n});\\n}else{\\ncallback({result:false,msg:\'请检查表单输入项\'});\\n}\\n})\\n},\\n//获取当前站群配置\\nget:function(id) {\\nvar that = this;\\nms.http.get(this.formURL.get.url, Object.assign({\\\"modelId\\\":that.modelId},this.formURL.get.params)).then(function (res) {\\nif(res.result&&res.data){\\nthat.form = res.data;\\n}\\n}).catch(function (err) {\\nconsole.log(err);\\n});\\n},\\n\\n},\\ncreated:function() {\\n//渲染create\\n\\nthis.get(this.form.linkId);\\n}\\n});\"}',null,'站群配置','','[\r\n    {\r\n    \"model\":\"siteEnable\",\r\n    \"key\":\"SITE_ENABLE\",\r\n    \"field\":\"SITE_ENABLE\",\r\n    \"javaType\":\"Boolean\",\r\n    \"jdbcType\":\"VARCHAR\",\r\n    \"name\":\"启用站群\",\r\n    \"type\":\"switch\",\r\n    \"length\":\"11\",\r\n    \"isShow\":\"false\"\r\n    }\r\n    ,{\r\n    \"model\":\"siteTables\",\r\n    \"key\":\"SITE_TABLES\",\r\n    \"field\":\"SITE_TABLES\",\r\n    \"javaType\":\"String\",\r\n    \"jdbcType\":\"VARCHAR\",\r\n    \"name\":\"站群涉及表\",\r\n    \"type\":\"textarea\",\r\n    \"length\":\"255\",\r\n    \"isShow\":\"false\"\r\n    }\r\n]\r\n\r\n','config',0,1 );
